(413, 1, '2015-01-09 06:00:22', '2015-01-09 06:00:22', '<div class="main_heading full_width">\n        <h1 class="red_color ">Advance Quality Infrastructure</h1>\n        <div class="main_headingBorder"></div>\n      </div>\n     <div class="plan_inner_part full_width top_margin">\n          <h2 class="red_color btm_padding1">Investing in Vital Public Infrastructure</h2>\n     <p class="btm_padding">Mayor Kristen Brown is committed to the continuation of significant investments in roads, our most critical public infrastructure, without raising taxes.   Mayor Brown’s plan includes:</p>\n     <a href="http://localhost/brown-for-columbus/wp-content/uploads/2015/01/Infrastructure1.png"><img src="http://localhost/brown-for-columbus/wp-content/uploads/2015/01/Infrastructure1.png" alt="Infrastructure" width="1001" height="320" class="alignnone size-full wp-image-412" /></a>\n     </div>\n     <div class="inner_text_container full_width top_margin">\n<h2>Repaired and Rebuilt Our Roads</h2>\n<ul>\n<li>Invest another $10+ million to repair our existing roads.</li>\n<li>Partner with Indiana Department of Transportation (INDOT) and Louisville & Indiana Railroad to construct an overpass over the railroad tracks at the intersection of state roads 46 and 11 to alleviate severe traffic backups caused by increasing train traffic.\n</li>\n<li>Construct an extension of Maple Street from Tipton Lane to U.S. 31 to give pedestrians, bicyclists and motorists significantly safer routes through the four-school campus area.</li>\n<li>Reconstruct the section of Rocky Ford between Taylor Road and Tally Road to widen lanes and add a two-way left turn lane, bicycle lanes, storm sewer system, sidewalks and pedestrian crossings at key locations.</li>\n</ul>\n</div>', 'Advance Quality Infrastructure', '', 'inherit', 'open', 'open', '', '294-autosave-v1', '', '', '2015-01-09 06:00:22', '2015-01-09 06:00:22', '', 294, 'http://localhost/brown-for-columbus/294-autosave-v1/', 0, 'revision', '', 0),
(414, 1, '2015-01-09 06:00:31', '2015-01-09 06:00:31', '<div class="main_heading full_width">\r\n        <h1 class="red_color ">Advance Quality Infrastructure</h1>\r\n        <div class="main_headingBorder"></div>\r\n      </div>\r\n     <div class="plan_inner_part full_width top_margin">\r\n          <h2 class="red_color btm_padding1">Investing in Vital Public Infrastructure</h2>\r\n     <p class="btm_padding">Mayor Kristen Brown is committed to the continuation of significant investments in roads, our most critical public infrastructure, without raising taxes.   Mayor Brown’s plan includes:</p>\r\n    <img src="http://localhost/brown-for-columbus/wp-content/uploads/2015/01/Infrastructure1.png" alt="Infrastructure" width="1001" height="320" class="alignnone size-full wp-image-412" />\r\n     </div>\r\n     <div class="inner_text_container full_width top_margin">\r\n<h2>Repaired and Rebuilt Our Roads</h2>\r\n<ul>\r\n<li>Invest another $10+ million to repair our existing roads.</li>\r\n<li>Partner with Indiana Department of Transportation (INDOT) and Louisville & Indiana Railroad to construct an overpass over the railroad tracks at the intersection of state roads 46 and 11 to alleviate severe traffic backups caused by increasing train traffic.\r\n</li>\r\n<li>Construct an extension of Maple Street from Tipton Lane to U.S. 31 to give pedestrians, bicyclists and motorists significantly safer routes through the four-school campus area.</li>\r\n<li>Reconstruct the section of Rocky Ford between Taylor Road and Tally Road to widen lanes and add a two-way left turn lane, bicycle lanes, storm sewer system, sidewalks and pedestrian crossings at key locations.</li>\r\n</ul>\r\n</div>', 'Advance Quality Infrastructure', '', 'inherit', 'open', 'open', '', '294-revision-v1', '', '', '2015-01-09 06:00:31', '2015-01-09 06:00:31', '', 294, 'http://localhost/brown-for-columbus/294-revision-v1/', 0, 'revision', '', 0),
